import React from 'react';
import './ProjectSection.css';
const checkboxes = document.querySelectorAll('input[type="checkbox"]');
    const items = document.getElementById('items').querySelectorAll('li');

    checkboxes.forEach(checkbox => checkbox.addEventListener('change', filterItems));

    function filterItems() {
      const selectedOptions = Array.from(checkboxes)
        .filter(checkbox => checkbox.checked)
        .map(checkbox => checkbox.value);

      items.forEach(item => {
        if (selectedOptions.length === 0 || selectedOptions.includes(item.classList[0])) {
          item.classList.add('show');
        } else {
          item.classList.remove('show');
        }
      });
    }
function ProjectSection() {

  return (

   <>
  <div>
  <label>Filter:</label>
    <div>
      <input type="checkbox" id="fruits" defaultValue="fruits" />
      <label htmlFor="fruits">Fruits</label>
    </div>
    <div>
      <input type="checkbox" id="cars" defaultValue="cars" />
      <label htmlFor="cars">Cars</label>
    </div>
    <div>
      <input type="checkbox" id="people" defaultValue="people" />
      <label htmlFor="people">People</label>
    </div>
  </div>
  <div id="items">
    <h2>Items:</h2>
    <ul>
      <li className="fruits">Apple</li>
      <li className="fruits">Banana</li>
      <li className="cars">Tesla</li>
      <li className="cars">BMW</li>
      <li className="people">John</li>
      <li className="people">Emily</li>
    </ul>
  </div>
</>
  );
}

export default ProjectSection;
